import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Heart, Pill, FileText, Shield, Zap, Users, ArrowRight } from 'lucide-react';

export default function LandingPage() {
  const navigate = useNavigate();
  const [activeTagline, setActiveTagline] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);

  const taglines = [
    "Protecting Lives, One Prescription at a Time",
    "Smart Medicine Tracking for Safer Healthcare",
    "Your Digital Guardian Against Expired Medicines"
  ];

  useEffect(() => {
    setIsLoaded(true);
    const interval = setInterval(() => {
      setActiveTagline((prev) => (prev + 1) % taglines.length);
    }, 3500);
    return () => clearInterval(interval);
  }, []);

  const cards = [
    {
      title: "Patient Registration",
      icon: Users,
      description: "Register new patients and manage their medical records securely with our advanced system",
      color: "from-blue-500 to-cyan-500",
      path: "/patient-registration",
      delay: "100"
    },
    {
      title: "Pharmacy Management",
      icon: Pill,
      description: "Upload bills, track inventory, manage stock, and monitor medicine expiry dates efficiently",
      color: "from-purple-500 to-pink-500",
      path: "/pharmacy",
      delay: "200"
    },
    {
      title: "Doctor Prescription",
      icon: FileText,
      description: "Create digital prescriptions with QR codes for seamless and secure medicine dispensing",
      color: "from-orange-500 to-red-500",
      path: "/doctor",
      delay: "300"
    }
  ];

  const features = [
    { icon: Shield, text: "Expiry Alert System" },
    { icon: Zap, text: "Real-time Tracking" },
    { icon: Heart, text: "Patient Safety First" }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 text-white overflow-hidden relative">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-72 h-72 bg-blue-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-20 right-10 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl animate-pulse" style={{animationDelay: '1s'}}></div>
        <div className="absolute top-1/2 left-1/2 w-64 h-64 bg-pink-500/10 rounded-full blur-3xl animate-pulse" style={{animationDelay: '2s'}}></div>
      </div>

      <div className="relative z-10 container mx-auto px-6 py-12">
        {/* Header with Logo */}
        <header className={`text-center mb-20 transition-all duration-1000 ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-10'}`}>
          <div className="flex items-center justify-center mb-8">
            <div className="relative group">
              <div className="absolute inset-0 bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 rounded-3xl blur-2xl opacity-60 group-hover:opacity-80 transition-opacity animate-pulse"></div>
              <div className="relative bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 p-6 rounded-3xl shadow-2xl transform hover:scale-110 hover:rotate-3 transition-all duration-500">
                <Heart className="w-20 h-20 text-white" strokeWidth={2.5} fill="white" />
              </div>
            </div>
          </div>

          <h1 className="text-7xl font-black mb-6 bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent tracking-tight">
            LifeTag
          </h1>

          {/* Animated Taglines */}
          <div className="h-24 flex items-center justify-center relative mb-8">
            {taglines.map((tagline, index) => (
              <p
                key={index}
                className={`text-2xl md:text-3xl text-blue-100 font-medium absolute transition-all duration-700 px-4 max-w-3xl ${
                  activeTagline === index
                    ? 'opacity-100 translate-y-0 scale-100'
                    : 'opacity-0 translate-y-4 scale-95'
                }`}
              >
                {tagline}
              </p>
            ))}
          </div>

          {/* Feature Pills */}
          <div className="flex flex-wrap justify-center gap-4">
            {features.map((feature, idx) => (
              <div
                key={idx}
                className="flex items-center gap-3 bg-white/10 backdrop-blur-md px-6 py-3 rounded-full border border-white/30 hover:bg-white/20 hover:scale-105 transition-all duration-300 cursor-pointer"
                style={{ animationDelay: `${idx * 150}ms` }}
              >
                <feature.icon className="w-5 h-5 text-blue-300" />
                <span className="text-sm font-semibold text-blue-100">{feature.text}</span>
              </div>
            ))}
          </div>
        </header>

        {/* Main Action Cards */}
        <div className="grid md:grid-cols-3 gap-8 max-w-7xl mx-auto mb-20">
          {cards.map((card, index) => {
            const Icon = card.icon;
            return (
              <div
                key={index}
                className={`group cursor-pointer transition-all duration-700 ${
                  isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
                }`}
                style={{ transitionDelay: `${card.delay}ms` }}
                onClick={() => navigate(card.path)}
              >
                <div className="relative h-full">
                  {/* Glow Effect */}
                  <div className={`absolute -inset-1 bg-gradient-to-r ${card.color} rounded-3xl blur-xl opacity-0 group-hover:opacity-40 transition-opacity duration-500`}></div>
                  
                  {/* Card Content */}
                  <div className="relative bg-white/10 backdrop-blur-lg border border-white/20 rounded-3xl p-10 h-full transform group-hover:scale-105 group-hover:-translate-y-2 transition-all duration-500 hover:shadow-2xl">
                    <div className={`bg-gradient-to-br ${card.color} w-20 h-20 rounded-2xl flex items-center justify-center mb-6 transform group-hover:rotate-12 group-hover:scale-110 transition-all duration-500 shadow-lg`}>
                      <Icon className="w-10 h-10 text-white" strokeWidth={2.5} />
                    </div>
                    
                    <h3 className="text-3xl font-bold mb-4 text-white">{card.title}</h3>
                    <p className="text-blue-200 leading-relaxed text-lg mb-6">{card.description}</p>
                    
                    <div className="flex items-center text-blue-300 group-hover:text-white transition-colors font-semibold">
                      <span>Get Started</span>
                      <ArrowRight className="w-5 h-5 ml-2 transform group-hover:translate-x-3 transition-transform duration-300" />
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Bottom Status */}
        <div className={`text-center transition-all duration-1000 delay-700 ${isLoaded ? 'opacity-100' : 'opacity-0'}`}>
          <div className="inline-flex items-center gap-3 bg-green-500/20 backdrop-blur-sm border border-green-500/40 px-8 py-4 rounded-full hover:bg-green-500/30 transition-all duration-300 cursor-pointer">
            <div className="relative">
              <div className="w-3 h-3 bg-green-400 rounded-full animate-ping absolute"></div>
              <div className="w-3 h-3 bg-green-400 rounded-full"></div>
            </div>
            <span className="text-green-300 font-semibold">System Active & Monitoring</span>
          </div>
        </div>
      </div>
    </div>
  );
}
