import axios from "axios";

const BASE = "http://localhost:5000/api";

export const uploadBill = (file) => {
  const fd = new FormData();
  fd.append("file", file);
  return axios.post(`${BASE}/upload_bill`, fd, { headers: {'Content-Type': 'multipart/form-data'} });
};

export const getInventory = () => axios.get(`${BASE}/inventory`);
export const getAlerts = () => axios.get(`${BASE}/alerts`);
export const deleteStock = (product_name,batch) => axios.post(`${BASE}/delete_stock`, {product_name, batch});
export const registerPatient = (data) => axios.post(`${BASE}/register_patient`, data);
export const getPatients = () => axios.get(`${BASE}/patients`);
export const createPrescription = (data) => axios.post(`${BASE}/create_prescription`, data);
export const getPrescription = (pid) => axios.get(`${BASE}/prescription/${pid}`);
export const scanQr = (payload) => axios.post(`${BASE}/scan_qr`, payload);
