import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Heart, Pill, FileText, Shield, Zap, Users } from 'lucide-react';

export default function LandingPage() {
  const navigate = useNavigate();
  const [activeTagline, setActiveTagline] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);

  const taglines = [
    "Protecting Lives, One Prescription at a Time",
    "Smart Medicine Tracking for Safer Healthcare",
    "Your Digital Guardian Against Expired Medicines"
  ];

  useEffect(() => {
    setIsLoaded(true);
    const interval = setInterval(() => {
      setActiveTagline((prev) => (prev + 1) % taglines.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const cards = [
    {
      title: "Patient Registration",
      icon: Users,
      description: "Register new patients and manage their medical records securely",
      color: "from-blue-500 to-cyan-500",
      path: "/patient-registration",
      delay: "100"
    },
    {
      title: "Pharmacy Management",
      icon: Pill,
      description: "Upload bills, track inventory, and manage medicine stock efficiently",
      color: "from-purple-500 to-pink-500",
      path: "/pharmacy",
      delay: "200"
    },
    {
      title: "Doctor Prescription",
      icon: FileText,
      description: "Create digital prescriptions with QR codes for easy dispensing",
      color: "from-orange-500 to-red-500",
      path: "/doctor",
      delay: "300"
    }
  ];

  const features = [
    { icon: Shield, text: "Expiry Alert System" },
    { icon: Zap, text: "Real-time Tracking" },
    { icon: Heart, text: "Patient Safety First" }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 text-white overflow-hidden">
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-72 h-72 bg-blue-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-20 right-10 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl animate-pulse" style={{animationDelay: '1s'}}></div>
      </div>

      <div className="relative z-10 container mx-auto px-6 py-12">
        <header className={`text-center mb-16 transition-all duration-1000 ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-10'}`}>
          <div className="flex items-center justify-center mb-6">
            <div className="relative">
              <div className="absolute inset-0 bg-gradient-to-r from-blue-400 to-purple-400 rounded-2xl blur-xl opacity-50 animate-pulse"></div>
              <div className="relative bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 p-4 rounded-2xl shadow-2xl transform hover:scale-105 transition-transform duration-300">
                <Heart className="w-16 h-16 text-white" strokeWidth={2.5} />
              </div>
            </div>
          </div>

          <h1 className="text-6xl font-bold mb-4 bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent">
            LifeTag
          </h1>

          <div className="h-20 flex items-center justify-center relative">
            {taglines.map((tagline, index) => (
              <p
                key={index}
                className={`text-xl md:text-2xl text-blue-200 absolute transition-all duration-700 px-4 ${
                  activeTagline === index
                    ? 'opacity-100 translate-y-0'
                    : 'opacity-0 translate-y-4'
                }`}
              >
                {tagline}
              </p>
            ))}
          </div>

          <div className="flex flex-wrap justify-center gap-4 mt-8">
            {features.map((feature, idx) => (
              <div
                key={idx}
                className="flex items-center gap-2 bg-white/10 backdrop-blur-sm px-4 py-2 rounded-full border border-white/20 hover:bg-white/20 transition-all duration-300"
                style={{ animationDelay: `${idx * 100}ms` }}
              >
                <feature.icon className="w-5 h-5 text-blue-300" />
                <span className="text-sm text-blue-100">{feature.text}</span>
              </div>
            ))}
          </div>
        </header>

        <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto mb-16">
          {cards.map((card, index) => {
            const Icon = card.icon;
            return (
              <div
                key={index}
                className={`group cursor-pointer transition-all duration-500 ${
                  isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
                }`}
                style={{ transitionDelay: `${card.delay}ms` }}
                onClick={() => navigate(card.path)}
              >
                <div className="relative h-full">
                  <div className={`absolute inset-0 bg-gradient-to-r ${card.color} rounded-2xl blur-xl opacity-0 group-hover:opacity-30 transition-opacity duration-500`}></div>
                  
                  <div className="relative bg-white/10 backdrop-blur-md border border-white/20 rounded-2xl p-8 h-full transform group-hover:scale-105 transition-all duration-300 hover:shadow-2xl">
                    <div className={`bg-gradient-to-r ${card.color} w-16 h-16 rounded-xl flex items-center justify-center mb-6 transform group-hover:rotate-6 transition-transform duration-300`}>
                      <Icon className="w-8 h-8 text-white" strokeWidth={2} />
                    </div>
                    
                    <h3 className="text-2xl font-bold mb-3 text-white">{card.title}</h3>
                    <p className="text-blue-200 leading-relaxed">{card.description}</p>
                    
                    <div className="mt-6 flex items-center text-blue-300 group-hover:text-blue-200 transition-colors">
                      <span className="text-sm font-semibold">Get Started</span>
                      <svg className="w-4 h-4 ml-2 transform group-hover:translate-x-2 transition-transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                      </svg>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        <div className={`text-center transition-all duration-1000 delay-500 ${isLoaded ? 'opacity-100' : 'opacity-0'}`}>
          <div className="inline-flex items-center gap-2 bg-green-500/20 border border-green-500/30 px-6 py-3 rounded-full">
            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
            <span className="text-green-300 text-sm font-medium">System Active & Monitoring</span>
          </div>
        </div>
      </div>
    </div>
  );
}
