// src/pages/DoctorCreatePrescription.js
import React, {useState, useEffect} from "react";
import { createPrescription, listPatients } from "../services/api";

export default function DoctorCreatePrescription(){
  const [patients, setPatients] = useState([]);
  const [patientId, setPatientId] = useState("");
  const [meds, setMeds] = useState([]);
  const [name, setName] = useState("");
  const [dose, setDose] = useState("");
  const [qty, setQty] = useState(1);
  const [result, setResult] = useState(null);

  useEffect(()=>{ listPatients().then(r=>setPatients(r)); },[]);

  function addMed(){
    if(!name) return;
    setMeds([...meds, {name, dosage: dose, quantity: Number(qty)}]);
    setName(""); setDose(""); setQty(1);
  }

  async function create(){
    if(!patientId || meds.length===0) { setResult("Select patient and add medicines"); return; }
    const res = await createPrescription("D001", patientId, meds);
    setResult(res);
  }

  return (
    <div style={{padding:20}}>
      <h2>Doctor: Create Prescription</h2>
      <label>Patient: </label>
      <select value={patientId} onChange={e=>setPatientId(e.target.value)}>
        <option value="">-- select patient --</option>
        {patients.map(p=> <option key={p.patient_id} value={p.patient_id}>{p.name} ({p.patient_id})</option>)}
      </select>
      <div style={{marginTop:10}}>
        <input placeholder="Medicine name" value={name} onChange={e=>setName(e.target.value)} />
        <input placeholder="Dosage" value={dose} onChange={e=>setDose(e.target.value)} />
        <input placeholder="Qty" type="number" value={qty} onChange={e=>setQty(e.target.value)} />
        <button onClick={addMed}>Add</button>
      </div>
      <div>
        <h4>Medicines</h4>
        <ul>{meds.map((m,i)=> <li key={i}>{m.name} • {m.dosage} • {m.quantity}</li>)}</ul>
      </div>
      <button onClick={create}>Create & Generate QR</button>
      <pre>{result && JSON.stringify(result, null, 2)}</pre>
    </div>
  );
}
