import React, { useState, useEffect } from "react";
import { getInventory, registerPatient, getPatients, createPrescription } from "../services/api";

export default function DoctorCreatePrescription() {
  const [inventory, setInventory] = useState([]);
  const [patients, setPatients] = useState([]);
  const [patientForm, setPatientForm] = useState({
    name: "",
    age: "",
    gender: "",
    contact: "",
    notes: "",
  });
  const [selectedPatient, setSelectedPatient] = useState("");
  const [doctor, setDoctor] = useState("Dr. Demo");
  const [pharmacyId, setPharmacyId] = useState("pharmacy_demo");
  const [cart, setCart] = useState([]);
  const [msg, setMsg] = useState("");
  const [qrPath, setQrPath] = useState("");

  // Fetch initial data
  useEffect(() => {
    getInventory()
      .then((r) => setInventory(r.data))
      .catch(() => setInventory([]));
    getPatients()
      .then((r) => setPatients(r.data))
      .catch(() => setPatients([]));
  }, []);

  // Add patient to system
  const addPatient = async () => {
    try {
      const res = await registerPatient(patientForm);
      setMsg("‚úÖ Patient registered: " + res.data.patient_id);
      setPatients((prev) => [
        ...prev,
        {
          ...patientForm,
          patient_id: res.data.patient_id,
          registered_at: new Date().toISOString(),
        },
      ]);
      setSelectedPatient(res.data.patient_id);
    } catch (err) {
      setMsg("‚ùå Error registering patient");
    }
  };

  // Add medicine to cart
  const addToCart = (item) => {
    const exists = cart.find(
      (c) => c.product_name === item.product_name && c.batch === item.batch
    );
    if (exists) return setMsg("‚ö†Ô∏è Already added to cart");
    setCart((prev) => [...prev, { ...item, qty: 1, dosage: "" }]);
  };

  // Create prescription
  const create = async () => {
    if (!selectedPatient) return setMsg("‚ö†Ô∏è Please select a patient");
    if (cart.length === 0) return setMsg("‚ö†Ô∏è Please add medicines");

    const meds = cart.map((c) => ({
      product_name: c.product_name,
      batch: c.batch,
      qty: c.qty,
      dosage: c.dosage,
    }));

    const payload = {
      patient_id: selectedPatient,
      doctor_name: doctor,
      pharmacy_id: pharmacyId,
      medications: meds,
    };

    try {
      const res = await createPrescription(payload);
      setMsg("‚úÖ Prescription created successfully!");
      setQrPath(res.data.qr_path); // backend returns path to saved QR image
    } catch (err) {
      console.error(err);
      setMsg("‚ùå Failed to create prescription");
    }
  };

  return (
    <div className="card" style={{ padding: 20 }}>
      <h2 style={{ color: "#0b6efd" }}>ü©∫ Doctor - Create Prescription</h2>

      <div style={{ display: "flex", gap: 20, marginTop: 10 }}>
        {/* Patient registration */}
        <div style={{ flex: 1 }}>
          <h4>Register New Patient</h4>
          <input
            placeholder="Name"
            value={patientForm.name}
            onChange={(e) => setPatientForm({ ...patientForm, name: e.target.value })}
          />
          <input
            placeholder="Age"
            value={patientForm.age}
            onChange={(e) => setPatientForm({ ...patientForm, age: e.target.value })}
          />
          <input
            placeholder="Gender"
            value={patientForm.gender}
            onChange={(e) => setPatientForm({ ...patientForm, gender: e.target.value })}
          />
          <input
            placeholder="Contact"
            value={patientForm.contact}
            onChange={(e) => setPatientForm({ ...patientForm, contact: e.target.value })}
          />
          <textarea
            placeholder="Notes"
            value={patientForm.notes}
            onChange={(e) => setPatientForm({ ...patientForm, notes: e.target.value })}
          />
          <button onClick={addPatient}>Register</button>
        </div>

        {/* Select existing patient and view inventory */}
        <div style={{ flex: 1 }}>
          <h4>Select Patient</h4>
          <select
            value={selectedPatient}
            onChange={(e) => setSelectedPatient(e.target.value)}
          >
            <option value="">-- choose --</option>
            {patients.map((p) => (
              <option key={p.patient_id} value={p.patient_id}>
                {p.name} ({p.contact})
              </option>
            ))}
          </select>

          <h4 style={{ marginTop: 10 }}>Available Medicines</h4>
          <div style={{ maxHeight: 220, overflow: "auto" }}>
            <table>
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Batch</th>
                  <th>Qty</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {inventory.map((r, idx) => (
                  <tr key={idx}>
                    <td>{r.product_name}</td>
                    <td>{r.batch}</td>
                    <td>{r.qty}</td>
                    <td>
                      <button onClick={() => addToCart(r)}>Add</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Cart section */}
      <h4 style={{ marginTop: 20 }}>Prescription Cart</h4>
      <div className="card">
        <table>
          <thead>
            <tr>
              <th>Product</th>
              <th>Batch</th>
              <th>Qty</th>
              <th>Dosage</th>
            </tr>
          </thead>
          <tbody>
            {cart.map((c, idx) => (
              <tr key={idx}>
                <td>{c.product_name}</td>
                <td>{c.batch}</td>
                <td>
                  <input
                    value={c.qty}
                    type="number"
                    onChange={(e) => {
                      const q = parseInt(e.target.value || 0);
                      setCart((prev) => {
                        const clone = [...prev];
                        clone[idx].qty = q;
                        return clone;
                      });
                    }}
                  />
                </td>
                <td>
                  <input
                    value={c.dosage}
                    placeholder="e.g. 1 tablet twice daily"
                    onChange={(e) => {
                      setCart((prev) => {
                        const clone = [...prev];
                        clone[idx].dosage = e.target.value;
                        return clone;
                      });
                    }}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button
          onClick={create}
          style={{ marginTop: 10, background: "#0b6efd", color: "white" }}
        >
          Create Prescription & Generate QR
        </button>
      </div>

      {/* Message + QR display */}
      <div style={{ color: "#0b6efd", marginTop: 10 }}>{msg}</div>

      {qrPath && (
        <div style={{ marginTop: 20 }}>
          <h4>Prescription QR Code</h4>
          <img
            src={`http://127.0.0.1:5000/static/qr/${qrPath}`}
            alt="Prescription QR"
            style={{ width: 200, border: "2px solid #0b6efd", padding: 10 }}
          />
          <p style={{ fontSize: 14, marginTop: 5 }}>
            üì± Patients or chemists can scan this QR to view full prescription details.
          </p>
        </div>
      )}
    </div>
  );
}
