import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FileText, UserPlus, ArrowLeft, Stethoscope } from 'lucide-react';

export default function DoctorDashboard() {
  const navigate = useNavigate();

  const options = [
    {
      title: "Register Patient",
      description: "Register a new patient to the system",
      icon: UserPlus,
      color: "from-blue-500 to-cyan-500",
      path: "/patient-registration"
    },
    {
      title: "Create Prescription",
      description: "Create digital prescription with QR code",
      icon: FileText,
      color: "from-orange-500 to-red-500",
      path: "/create-prescription"
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-orange-900 to-slate-900 text-white py-12 px-6">
      <div className="max-w-5xl mx-auto">
        {/* Header */}
        <div className="mb-12">
          <button
            onClick={() => navigate('/')}
            className="flex items-center gap-2 text-orange-300 hover:text-orange-200 transition-colors mb-6 group"
          >
            <ArrowLeft className="w-5 h-5 group-hover:-translate-x-1 transition-transform" />
            <span>Back to Home</span>
          </button>

          <div className="flex items-center gap-4">
            <div className="bg-gradient-to-br from-orange-500 to-red-500 p-4 rounded-2xl shadow-xl">
              <Stethoscope className="w-10 h-10 text-white" />
            </div>
            <div>
              <h1 className="text-5xl font-bold bg-gradient-to-r from-orange-400 to-red-400 bg-clip-text text-transparent">
                Doctor Dashboard
              </h1>
              <p className="text-orange-200 mt-2">Manage patients and create prescriptions</p>
            </div>
          </div>
        </div>

        {/* Options */}
        <div className="grid md:grid-cols-2 gap-8">
          {options.map((option, idx) => {
            const Icon = option.icon;
            return (
              <div
                key={idx}
                onClick={() => navigate(option.path)}
                className="group cursor-pointer"
              >
                <div className="relative h-full">
                  <div className={`absolute -inset-1 bg-gradient-to-r ${option.color} rounded-3xl blur-xl opacity-0 group-hover:opacity-40 transition-opacity duration-500`}></div>
                  
                  <div className="relative bg-white/10 backdrop-blur-lg border border-white/20 rounded-3xl p-10 h-full transform group-hover:scale-105 group-hover:-translate-y-2 transition-all duration-500">
                    <div className={`bg-gradient-to-br ${option.color} w-20 h-20 rounded-2xl flex items-center justify-center mb-6 transform group-hover:rotate-12 group-hover:scale-110 transition-all duration-500 shadow-lg`}>
                      <Icon className="w-10 h-10 text-white" strokeWidth={2.5} />
                    </div>
                    
                    <h3 className="text-3xl font-bold mb-4 text-white">{option.title}</h3>
                    <p className="text-orange-200 leading-relaxed text-lg">{option.description}</p>
                    
                    <div className="flex items-center text-orange-300 group-hover:text-white transition-colors font-semibold mt-6">
                      <span>Get Started</span>
                      <svg className="w-5 h-5 ml-2 transform group-hover:translate-x-3 transition-transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                      </svg>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
