// src/services/api.js
const BASE = "http://10.251.93.93:5000"; // use your backend IP

export async function uploadBill(file, chemistId="C001") {
  const form = new FormData();
  form.append("file", file);
  form.append("chemist_id", chemistId);
  const res = await fetch(`${BASE}/upload_bill`, { method: "POST", body: form });
  return res.json();
}

export async function createPrescription(doctorId, patientId, meds) {
  const res = await fetch(`${BASE}/create_prescription`, {
    method: "POST",
    headers: {"Content-Type":"application/json"},
    body: JSON.stringify({ doctor_id: doctorId, patient_id: patientId, medicines: meds })
  });
  return res.json();
}

export async function scanDispense(code, chemistId="C001") {
  const res = await fetch(`${BASE}/scan_dispense`, {
    method: "POST",
    headers: {"Content-Type":"application/json"},
    body: JSON.stringify({ code, chemist_id: chemistId })
  });
  return res.json();
}

export async function listPatients() {
  const res = await fetch(`${BASE}/list_patients`);
  return res.json();
}
