import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getInventory, getPatients, createPrescription } from '../services/api';
import { ArrowLeft, Plus, Trash2, FileText, CheckCircle, AlertCircle, QrCode } from 'lucide-react';

export default function CreatePrescription() {
  const navigate = useNavigate();
  const [inventory, setInventory] = useState([]);
  const [patients, setPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState('');
  const [doctor, setDoctor] = useState('Dr Demo');
  const [cart, setCart] = useState([]);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [prescriptionId, setPrescriptionId] = useState('');
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    getInventory().then(r => setInventory(r.data)).catch(() => setInventory([]));
    getPatients().then(r => setPatients(r.data)).catch(() => setPatients([]));
  }, []);

  const addToCart = (item) => {
    if (cart.some(c => c.product_name === item.product_name && c.batch === item.batch)) {
      setMessage({ type: 'error', text: 'Medicine already in cart' });
      setTimeout(() => setMessage({ type: '', text: '' }), 2000);
      return;
    }
    setCart([...cart, { ...item, qty: 1, dosage: 'As directed' }]);
  };

  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  const updateCart = (index, field, value) => {
    const newCart = [...cart];
    newCart[index][field] = value;
    setCart(newCart);
  };

  const handleCreate = async () => {
    if (!selectedPatient) {
      setMessage({ type: 'error', text: 'Please select a patient' });
      return;
    }
    if (cart.length === 0) {
      setMessage({ type: 'error', text: 'Please add medicines to cart' });
      return;
    }

    const newId = 'RX' + Date.now();
    setPrescriptionId(newId);

    const payload = {
      prescription_id: newId,
      patient_id: selectedPatient,
      doctor_name: doctor,
      pharmacy_id: 'pharmacy_demo',
      medications: cart.map(c => ({
        product_name: c.product_name,
        batch: c.batch,
        qty: c.qty,
        dosage: c.dosage
      }))
    };

    setLoading(true);
    try {
      await createPrescription(payload);
      setMessage({ type: 'success', text: 'Prescription created successfully!' });
    } catch (err) {
      setMessage({ type: 'error', text: 'Failed to create prescription' });
      setPrescriptionId('');
    } finally {
      setLoading(false);
    }
  };

  const filteredInventory = inventory.filter(item => 
    item.product_name?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-red-900 to-slate-900 text-white py-8 px-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <button
            onClick={() => navigate('/doctor')}
            className="flex items-center gap-2 text-red-300 hover:text-red-200 transition-colors mb-6 group"
          >
            <ArrowLeft className="w-5 h-5 group-hover:-translate-x-1 transition-transform" />
            <span>Back to Doctor Dashboard</span>
          </button>

          <div className="flex items-center gap-4">
            <div className="bg-gradient-to-br from-orange-500 to-red-500 p-4 rounded-2xl shadow-xl">
              <FileText className="w-10 h-10 text-white" />
            </div>
            <div>
              <h1 className="text-5xl font-bold bg-gradient-to-r from-orange-400 to-red-400 bg-clip-text text-transparent">
                Create Prescription
              </h1>
              <p className="text-red-200 mt-2">Generate digital prescription with QR code</p>
            </div>
          </div>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Left: Patient & Doctor Info */}
          <div className="lg:col-span-1 space-y-6">
            <div className="bg-white/10 backdrop-blur-lg border border-white/20 rounded-3xl p-6 shadow-2xl">
              <h3 className="text-2xl font-bold mb-4">Patient Details</h3>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-red-200 text-sm font-semibold mb-2">Select Patient</label>
                  <select
                    value={selectedPatient}
                    onChange={(e) => setSelectedPatient(e.target.value)}
                    className="w-full bg-white/5 border border-white/20 rounded-xl px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-red-500"
                  >
                    <option value="" className="bg-slate-800">-- Choose Patient --</option>
                    {patients.map(p => (
                      <option key={p.patient_id} value={p.patient_id} className="bg-slate-800">
                        {p.name} ({p.contact})
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-red-200 text-sm font-semibold mb-2">Doctor Name</label>
                  <input
                    type="text"
                    value={doctor}
                    onChange={(e) => setDoctor(e.target.value)}
                    className="w-full bg-white/5 border border-white/20 rounded-xl px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-red-500"
                  />
                </div>
              </div>
            </div>

            {/* Cart Summary */}
            <div className="bg-white/10 backdrop-blur-lg border border-white/20 rounded-3xl p-6 shadow-2xl">
              <h3 className="text-2xl font-bold mb-4">Cart Summary</h3>
              <div className="space-y-2">
                <div className="flex justify-between text-red-200">
                  <span>Total Items:</span>
                  <span className="font-bold text-white">{cart.length}</span>
                </div>
                <div className="flex justify-between text-red-200">
                  <span>Total Quantity:</span>
                  <span className="font-bold text-white">{cart.reduce((sum, item) => sum + parseInt(item.qty), 0)}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Middle: Inventory */}
          <div className="lg:col-span-1">
            <div className="bg-white/10 backdrop-blur-lg border border-white/20 rounded-3xl p-6 shadow-2xl h-full">
              <h3 className="text-2xl font-bold mb-4">Available Medicines</h3>
              
              <input
                type="text"
                placeholder="Search medicines..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full bg-white/5 border border-white/20 rounded-xl px-4 py-2 text-white placeholder-red-300/50 focus:outline-none focus:ring-2 focus:ring-red-500 mb-4"
              />

              <div className="space-y-2 max-h-96 overflow-y-auto">
                {filteredInventory
                  .filter(item => item.product_name && item.product_name.trim() !== '')
                  .map((item, idx) => (
                    <div
                      key={idx}
                      className="bg-white/5 hover:bg-white/10 rounded-xl p-4 cursor-pointer transition-all border border-white/10"
                      onClick={() => addToCart(item)}
                    >
                      <div className="flex justify-between items-start">
                        <div>
                          <p className="font-semibold text-white">{item.product_name}</p>
                          <p className="text-sm text-red-200">Batch: {item.batch}</p>
                          <p className="text-sm text-red-200">Stock: {item.qty}</p>
                        </div>
                        <Plus className="w-5 h-5 text-red-300" />
                      </div>
                    </div>
                  ))}
              </div>
            </div>
          </div>

          {/* Right: Cart */}
          <div className="lg:col-span-1">
            <div className="bg-white/10 backdrop-blur-lg border border-white/20 rounded-3xl p-6 shadow-2xl h-full">
              <h3 className="text-2xl font-bold mb-4">Prescription Cart</h3>
              
              {cart.length === 0 ? (
                <div className="text-center py-12 text-red-300">
                  <FileText className="w-16 h-16 mx-auto mb-4 opacity-50" />
                  <p>No medicines added</p>
                </div>
              ) : (
                <div className="space-y-4 mb-6 max-h-96 overflow-y-auto">
                  {cart.map((item, idx) => (
                    <div key={idx} className="bg-white/5 rounded-xl p-4 border border-white/10">
                      <div className="flex justify-between items-start mb-3">
                        <div>
                          <p className="font-semibold text-white">{item.product_name}</p>
                          <p className="text-sm text-red-200">Batch: {item.batch}</p>
                        </div>
                        <button
                          onClick={() => removeFromCart(idx)}
                          className="text-red-400 hover:text-red-300"
                        >
                          <Trash2 className="w-5 h-5" />
                        </button>
                      </div>
                      
                      <div className="grid grid-cols-2 gap-3">
                        <div>
                          <label className="block text-xs text-red-200 mb-1">Quantity</label>
                          <input
                            type="number"
                            min="1"
                            value={item.qty}
                            onChange={(e) => updateCart(idx, 'qty', parseInt(e.target.value) || 1)}
                            className="w-full bg-white/5 border border-white/20 rounded-lg px-3 py-2 text-white text-sm focus:outline-none focus:ring-2 focus:ring-red-500"
                          />
                        </div>
                        <div>
                          <label className="block text-xs text-red-200 mb-1">Dosage</label>
                          <input
                            type="text"
                            value={item.dosage}
                            onChange={(e) => updateCart(idx, 'dosage', e.target.value)}
                            className="w-full bg-white/5 border border-white/20 rounded-lg px-3 py-2 text-white text-sm focus:outline-none focus:ring-2 focus:ring-red-500"
                          />
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              {/* Message */}
              {message.text && (
                <div className={`mb-4 flex items-center gap-3 p-4 rounded-xl ${
                  message.type === 'success'
                    ? 'bg-green-500/20 border border-green-500/40 text-green-300'
                    : 'bg-red-500/20 border border-red-500/40 text-red-300'
                }`}>
                  {message.type === 'success' ? <CheckCircle className="w-5 h-5" /> : <AlertCircle className="w-5 h-5" />}
                  <span className="font-medium text-sm">{message.text}</span>
                </div>
              )}

              {/* Create Button */}
              <button
                onClick={handleCreate}
                disabled={loading || cart.length === 0 || !selectedPatient}
                className="w-full bg-gradient-to-r from-orange-500 to-red-500 hover:from-orange-600 hover:to-red-600 text-white font-bold py-4 rounded-xl transition-all disabled:opacity-50 disabled:cursor-not-allowed shadow-xl"
              >
                {loading ? 'Creating...' : 'Create Prescription & Generate QR'}
              </button>

              {/* QR Code Display */}
              {prescriptionId && (
                <div className="mt-6 text-center bg-white/5 rounded-2xl p-6 border border-white/10">
                  <QrCode className="w-8 h-8 mx-auto mb-3 text-green-400" />
                  <p className="text-sm text-red-200 mb-3">Prescription ID: <span className="font-mono font-bold text-white">{prescriptionId}</span></p>
                  <img
                  src={`http://localhost:5000/qrcodes/${prescriptionId}.png`}
                    alt="Prescription QR"
                    className="mx-auto rounded-xl shadow-lg border-4 border-white/20"
                    width="200"
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'block';
                    }}
                  />
                  <p className="text-xs text-red-300 mt-3" style={{display: 'none'}}>
                    QR code will be available shortly
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
