import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { uploadBill, getInventory, getAlerts, deleteStock, getPrescription, dispensePrescription } from '../services/api';
import { Upload, Package, AlertTriangle, Trash2, ArrowLeft, Search, QrCode, Eye, Heart } from 'lucide-react';

export default function PharmacyDashboard() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('upload');
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [inventory, setInventory] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [prescriptionId, setPrescriptionId] = useState('');
  const [prescriptionData, setPrescriptionData] = useState(null);
  const [loading, setLoading] = useState(false);

  const loadData = () => {
    getInventory().then(r => setInventory(r.data)).catch(() => setInventory([]));
    getAlerts().then(r => setAlerts(r.data)).catch(() => setAlerts([]));
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleUpload = async () => {
    if (!file) {
      setMessage('⚠️ Please select a file first!');
      return;
    }

    setLoading(true);
    try {
      const res = await uploadBill(file);
      setMessage('✅ Upload successful! ' + (res.data.imported || '') + ' items added.');
      setFile(null);
      loadData();
    } catch (err) {
      setMessage('❌ Upload failed: ' + (err?.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (product_name, batch) => {
    if (!window.confirm(`Delete ${product_name} (Batch: ${batch})?`)) return;

    try {
      await deleteStock(product_name, batch);
      setMessage('✅ Deleted successfully!');
      loadData();
    } catch (err) {
      setMessage('❌ Delete failed: ' + err.message);
    }
  };

  const handleViewPrescription = async () => {
    if (!prescriptionId.trim()) return;

    setLoading(true);
    try {
      const res = await getPrescription(prescriptionId);
      setPrescriptionData(res.data);
      setMessage('');
    } catch (err) {
      setMessage('❌ Prescription not found');
      setPrescriptionData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleDispense = async () => {
    if (!prescriptionId.trim()) return;

    if (!window.confirm('Dispense this prescription?')) return;

    setLoading(true);
    try {
      await dispensePrescription({ prescription_id: prescriptionId, pharmacy_id: 'pharmacy_demo' });
      setMessage('✅ Prescription dispensed successfully!');
      setPrescriptionData(null);
      setPrescriptionId('');
      loadData();
    } catch (err) {
      setMessage('❌ Dispensing failed: ' + (err?.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const filteredInventory = inventory.filter(item => 
    item.product_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.batch?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-teal-50 to-slate-100">
      {/* Header */}
      <header className="bg-white border-b border-slate-200 shadow-sm sticky top-0 z-50">
        <div className="container mx-auto px-6 py-4">
          <div className="flex items-center gap-3">
            <div className="bg-blue-600 p-2.5 rounded-lg shadow-md">
              <Heart className="w-7 h-7 text-white" fill="white" strokeWidth={2} />
            </div>
            <div>
              <h1 className="text-2xl font-bold text-slate-800">LifeTag</h1>
              <p className="text-xs text-slate-500 font-medium">Medical Management System</p>
            </div>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-6 py-8">
        {/* Back Button */}
        <button
          onClick={() => navigate('/')}
          className="flex items-center gap-2 text-slate-600 hover:text-slate-800 transition-colors mb-6 group"
        >
          <ArrowLeft className="w-5 h-5 group-hover:-translate-x-1 transition-transform" />
          <span className="font-medium">Back to Home</span>
        </button>

        {/* Page Title */}
        <div className="flex items-center gap-4 mb-8">
          <div className="bg-teal-50 border border-teal-200 p-3 rounded-xl">
            <Package className="w-8 h-8 text-teal-600" strokeWidth={2} />
          </div>
          <div>
            <h2 className="text-3xl font-bold text-slate-800">Pharmacy Dashboard</h2>
            <p className="text-slate-600 mt-1">Manage inventory, alerts, and prescriptions</p>
          </div>
        </div>

        {/* Tabs */}
        <div className="flex gap-3 mb-8 overflow-x-auto pb-2">
          {[
            { id: 'upload', label: 'Upload Bills', icon: Upload },
            { id: 'inventory', label: 'Inventory', icon: Package },
            { id: 'alerts', label: 'Alerts', icon: AlertTriangle },
            { id: 'scan', label: 'Scan & Dispense', icon: QrCode }
          ].map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center gap-2 px-5 py-3 rounded-lg font-semibold transition-all whitespace-nowrap border ${
                activeTab === tab.id
                  ? 'bg-teal-600 text-white border-teal-600 shadow-md'
                  : 'bg-white text-slate-600 border-slate-200 hover:border-teal-300 hover:text-teal-600'
              }`}
            >
              <tab.icon className="w-5 h-5" />
              {tab.label}
            </button>
          ))}
        </div>

        {/* Message */}
        {message && (
          <div className={`mb-6 p-4 rounded-lg border font-medium ${
            message.includes('✅') 
              ? 'bg-green-50 border-green-200 text-green-700' 
              : 'bg-red-50 border-red-200 text-red-700'
          }`}>
            {message}
          </div>
        )}

        {/* Tab Content */}
        <div className="bg-white border border-slate-200 rounded-2xl p-8 shadow-md">
          {/* Upload Tab */}
          {activeTab === 'upload' && (
            <div>
              <h3 className="text-2xl font-bold text-slate-800 mb-6">Upload Medicine Bill</h3>
              <div className="space-y-4">
                <div className="border-2 border-dashed border-slate-300 rounded-xl p-12 text-center hover:border-teal-400 hover:bg-teal-50/30 transition-all">
                  <Upload className="w-16 h-16 mx-auto mb-4 text-slate-400" />
                  <input
                    type="file"
                    onChange={(e) => setFile(e.target.files[0])}
                    className="hidden"
                    id="file-upload"
                    accept=".csv,.png,.jpg,.jpeg"
                  />
                  <label
                    htmlFor="file-upload"
                    className="cursor-pointer inline-block bg-teal-600 hover:bg-teal-700 text-white font-semibold py-3 px-8 rounded-lg transition-all shadow-md hover:shadow-lg"
                  >
                    Choose File
                  </label>
                  {file && (
                    <p className="mt-4 text-slate-600 font-medium">Selected: <span className="text-teal-600">{file.name}</span></p>
                  )}
                </div>
                <button
                  onClick={handleUpload}
                  disabled={loading || !file}
                  className="w-full bg-teal-600 hover:bg-teal-700 text-white font-bold py-4 rounded-lg transition-all disabled:opacity-50 disabled:cursor-not-allowed shadow-md hover:shadow-lg"
                >
                  {loading ? 'Uploading...' : 'Upload Bill'}
                </button>
              </div>
            </div>
          )}

          {/* Inventory Tab */}
          {activeTab === 'inventory' && (
            <div>
              <div className="flex items-center justify-between mb-6">
                <h3 className="text-2xl font-bold text-slate-800">Medicine Inventory</h3>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
                  <input
                    type="text"
                    placeholder="Search medicines..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 pr-4 py-2 bg-slate-50 border border-slate-300 rounded-lg text-slate-800 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  />
                </div>
              </div>

              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b-2 border-slate-200">
                      <th className="text-left py-4 px-4 text-slate-700 font-bold">Product</th>
                      <th className="text-left py-4 px-4 text-slate-700 font-bold">Batch</th>
                      <th className="text-left py-4 px-4 text-slate-700 font-bold">Expiry</th>
                      <th className="text-left py-4 px-4 text-slate-700 font-bold">Qty</th>
                      <th className="text-left py-4 px-4 text-slate-700 font-bold">Status</th>
                      <th className="text-left py-4 px-4 text-slate-700 font-bold">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredInventory.length === 0 ? (
                      <tr>
                        <td colSpan="6" className="text-center py-12 text-slate-500">
                          No inventory items found
                        </td>
                      </tr>
                    ) : (
                      filteredInventory
                        .filter(item => item.product_name && item.product_name.trim() !== '')
                        .map((item, idx) => (
                          <tr key={idx} className="border-b border-slate-100 hover:bg-slate-50 transition-colors">
                            <td className="py-4 px-4 text-slate-800 font-medium">{item.product_name}</td>
                            <td className="py-4 px-4 text-slate-600">{item.batch}</td>
                            <td className="py-4 px-4 text-slate-600">{item.exp}</td>
                            <td className="py-4 px-4">
                              <span className={`px-3 py-1 rounded-full text-sm font-semibold ${
                                parseInt(item.qty) < 10 
                                  ? 'bg-red-50 text-red-600 border border-red-200' 
                                  : 'bg-green-50 text-green-600 border border-green-200'
                              }`}>
                                {item.qty}
                              </span>
                            </td>
                            <td className="py-4 px-4">
                              {item.expired ? (
                                <span className="px-3 py-1 rounded-full bg-red-50 text-red-600 border border-red-200 text-sm font-semibold">
                                  Expired
                                </span>
                              ) : item.days_to_expiry !== null && item.days_to_expiry < 15 ? (
                                <span className="px-3 py-1 rounded-full bg-orange-50 text-orange-600 border border-orange-200 text-sm font-semibold">
                                  {item.days_to_expiry}d left
                                </span>
                              ) : (
                                <span className="px-3 py-1 rounded-full bg-green-50 text-green-600 border border-green-200 text-sm font-semibold">
                                  Good
                                </span>
                              )}
                            </td>
                            <td className="py-4 px-4">
                              <button
                                onClick={() => handleDelete(item.product_name, item.batch)}
                                className="flex items-center gap-2 bg-red-50 hover:bg-red-100 text-red-600 border border-red-200 px-4 py-2 rounded-lg transition-all font-medium"
                              >
                                <Trash2 className="w-4 h-4" />
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Alerts Tab */}
          {activeTab === 'alerts' && (
            <div>
              <h3 className="text-2xl font-bold text-slate-800 mb-6">Expiry Alerts</h3>
              {alerts.length === 0 ? (
                <div className="text-center py-16">
                  <div className="bg-slate-50 border border-slate-200 w-20 h-20 rounded-full flex items-center justify-center mx-auto mb-4">
                    <AlertTriangle className="w-10 h-10 text-slate-400" />
                  </div>
                  <p className="text-slate-500 text-lg font-medium">No active alerts</p>
                  <p className="text-slate-400 text-sm mt-1">All medicines are within safe expiry dates</p>
                </div>
              ) : (
                <div className="grid md:grid-cols-2 gap-4">
                  {alerts.map(alert => (
                    <div
                      key={alert.alert_id}
                      className="bg-red-50 border-l-4 border-red-500 rounded-lg p-6 hover:bg-red-100 transition-all"
                    >
                      <div className="flex items-start gap-4">
                        <div className="bg-red-100 p-2 rounded-lg">
                          <AlertTriangle className="w-6 h-6 text-red-600" />
                        </div>
                        <div className="flex-1">
                          <h4 className="text-lg font-bold text-slate-800 mb-2">{alert.product_name}</h4>
                          <div className="space-y-1 text-sm text-slate-600">
                            <p><span className="font-semibold">Batch:</span> {alert.batch}</p>
                            <p><span className="font-semibold">Expiry:</span> {alert.exp}</p>
                            <p><span className="font-semibold">Days Left:</span> {alert.days_to_expiry}</p>
                            <p className="text-red-600 font-semibold mt-2">{alert.alert_type}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* Scan & Dispense Tab */}
          {activeTab === 'scan' && (
            <div>
              <h3 className="text-2xl font-bold text-slate-800 mb-6">Scan QR & Dispense</h3>
              
              <div className="space-y-6">
                <div>
                  <label className="block text-slate-700 font-semibold mb-2">Prescription ID</label>
                  <div className="flex gap-4">
                    <input
                      type="text"
                      value={prescriptionId}
                      onChange={(e) => setPrescriptionId(e.target.value)}
                      placeholder="Enter or scan prescription ID"
                      className="flex-1 bg-slate-50 border border-slate-300 rounded-lg px-4 py-3 text-slate-800 placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                    />
                    <button
                      onClick={handleViewPrescription}
                      disabled={loading || !prescriptionId.trim()}
                      className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-3 rounded-lg transition-all disabled:opacity-50 shadow-md"
                    >
                      <Eye className="w-5 h-5" />
                      View
                    </button>
                  </div>
                </div>

                {/* Prescription Details */}
                {prescriptionData && (
                  <div className="bg-slate-50 border border-slate-200 rounded-xl p-6">
                    <h4 className="text-xl font-bold text-slate-800 mb-4">Prescription Details</h4>
                    
                    <div className="grid md:grid-cols-2 gap-4 mb-6">
                      <div className="bg-white border border-slate-200 rounded-lg p-4">
                        <p className="text-slate-500 text-sm font-medium mb-1">Prescription ID</p>
                        <p className="text-slate-800 font-bold">{prescriptionData.prescription_id}</p>
                      </div>
                      <div className="bg-white border border-slate-200 rounded-lg p-4">
                        <p className="text-slate-500 text-sm font-medium mb-1">Patient ID</p>
                        <p className="text-slate-800 font-bold">{prescriptionData.patient_id}</p>
                      </div>
                      <div className="bg-white border border-slate-200 rounded-lg p-4">
                        <p className="text-slate-500 text-sm font-medium mb-1">Doctor</p>
                        <p className="text-slate-800 font-bold">{prescriptionData.doctor_name}</p>
                      </div>
                      <div className="bg-white border border-slate-200 rounded-lg p-4">
                        <p className="text-slate-500 text-sm font-medium mb-1">Status</p>
                        <span className={`inline-block px-3 py-1 rounded-full text-sm font-semibold border ${
                          prescriptionData.status === 'dispensed' 
                            ? 'bg-green-50 text-green-600 border-green-200' 
                            : 'bg-blue-50 text-blue-600 border-blue-200'
                        }`}>
                          {prescriptionData.status}
                        </span>
                      </div>
                    </div>

                    <div className="mb-6">
                      <h5 className="text-lg font-semibold text-slate-800 mb-3">Medications</h5>
                      <div className="space-y-3">
                        {prescriptionData.medications?.map((med, idx) => (
                          <div key={idx} className="bg-white border border-slate-200 rounded-lg p-4 flex justify-between items-center">
                            <div>
                              <p className="text-slate-800 font-bold">{med.product_name}</p>
                              <p className="text-slate-600 text-sm">Batch: {med.batch} | Dosage: {med.dosage || 'As directed'}</p>
                            </div>
                            <span className="bg-teal-50 text-teal-600 border border-teal-200 px-3 py-1 rounded-full text-sm font-semibold">
                              Qty: {med.qty}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>

                    {prescriptionData.status !== 'dispensed' && (
                      <button
                        onClick={handleDispense}
                        disabled={loading}
                        className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-4 rounded-lg transition-all disabled:opacity-50 shadow-md hover:shadow-lg"
                      >
                        {loading ? 'Dispensing...' : 'Dispense Prescription'}
                      </button>
                    )}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
