import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { uploadBill, getInventory, getAlerts, deleteStock, getPrescription, dispensePrescription } from '../services/api';
import { Upload, Package, AlertTriangle, Trash2, ArrowLeft, Search, QrCode, Eye } from 'lucide-react';

export default function PharmacyDashboard() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('upload');
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [inventory, setInventory] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [prescriptionId, setPrescriptionId] = useState('');
  const [prescriptionData, setPrescriptionData] = useState(null);
  const [loading, setLoading] = useState(false);

  const loadData = () => {
    getInventory().then(r => setInventory(r.data)).catch(() => setInventory([]));
    getAlerts().then(r => setAlerts(r.data)).catch(() => setAlerts([]));
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleUpload = async () => {
    if (!file) {
      setMessage('⚠️ Please select a file first!');
      return;
    }

    setLoading(true);
    try {
      const res = await uploadBill(file);
      setMessage('✅ Upload successful! ' + (res.data.imported || '') + ' items added.');
      setFile(null);
      loadData();
    } catch (err) {
      setMessage('❌ Upload failed: ' + (err?.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (product_name, batch) => {
    if (!window.confirm(`Delete ${product_name} (Batch: ${batch})?`)) return;

    try {
      await deleteStock(product_name, batch);
      setMessage('✅ Deleted successfully!');
      loadData();
    } catch (err) {
      setMessage('❌ Delete failed: ' + err.message);
    }
  };

  const handleViewPrescription = async () => {
    if (!prescriptionId.trim()) return;

    setLoading(true);
    try {
      const res = await getPrescription(prescriptionId);
      setPrescriptionData(res.data);
      setMessage('');
    } catch (err) {
      setMessage('❌ Prescription not found');
      setPrescriptionData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleDispense = async () => {
    if (!prescriptionId.trim()) return;

    if (!window.confirm('Dispense this prescription?')) return;

    setLoading(true);
    try {
      await dispensePrescription({ prescription_id: prescriptionId, pharmacy_id: 'pharmacy_demo' });
      setMessage('✅ Prescription dispensed successfully!');
      setPrescriptionData(null);
      setPrescriptionId('');
      loadData();
    } catch (err) {
      setMessage('❌ Dispensing failed: ' + (err?.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const filteredInventory = inventory.filter(item => 
    item.product_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.batch?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 text-white py-8 px-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <button
            onClick={() => navigate('/')}
            className="flex items-center gap-2 text-purple-300 hover:text-purple-200 transition-colors mb-6 group"
          >
            <ArrowLeft className="w-5 h-5 group-hover:-translate-x-1 transition-transform" />
            <span>Back to Home</span>
          </button>

          <div className="flex items-center gap-4">
            <div className="bg-gradient-to-br from-purple-500 to-pink-500 p-4 rounded-2xl shadow-xl">
              <Package className="w-10 h-10 text-white" />
            </div>
            <div>
              <h1 className="text-5xl font-bold bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
                Pharmacy Dashboard
              </h1>
              <p className="text-purple-200 mt-2">Manage inventory, alerts, and prescriptions</p>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div className="flex gap-4 mb-8 overflow-x-auto">
          {[
            { id: 'upload', label: 'Upload Bills', icon: Upload },
            { id: 'inventory', label: 'Inventory', icon: Package },
            { id: 'alerts', label: 'Alerts', icon: AlertTriangle },
            { id: 'scan', label: 'Scan & Dispense', icon: QrCode }
          ].map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center gap-2 px-6 py-3 rounded-xl font-semibold transition-all whitespace-nowrap ${
                activeTab === tab.id
                  ? 'bg-purple-500 text-white shadow-xl scale-105'
                  : 'bg-white/10 text-purple-200 hover:bg-white/20'
              }`}
            >
              <tab.icon className="w-5 h-5" />
              {tab.label}
            </button>
          ))}
        </div>

        {/* Message */}
        {message && (
          <div className={`mb-6 p-4 rounded-xl ${
            message.includes('✅') ? 'bg-green-500/20 border border-green-500/40 text-green-300' : 'bg-red-500/20 border border-red-500/40 text-red-300'
          }`}>
            {message}
          </div>
        )}

        {/* Tab Content */}
        <div className="bg-white/10 backdrop-blur-lg border border-white/20 rounded-3xl p-8 shadow-2xl">
          {/* Upload Tab */}
          {activeTab === 'upload' && (
            <div>
              <h2 className="text-3xl font-bold mb-6">Upload Medicine Bill</h2>
              <div className="space-y-4">
                <div className="border-2 border-dashed border-purple-400/50 rounded-2xl p-8 text-center hover:border-purple-400 transition-colors">
                  <Upload className="w-16 h-16 mx-auto mb-4 text-purple-400" />
                  <input
                    type="file"
                    onChange={(e) => setFile(e.target.files[0])}
                    className="hidden"
                    id="file-upload"
                    accept=".csv,.png,.jpg,.jpeg"
                  />
                  <label
                    htmlFor="file-upload"
                    className="cursor-pointer inline-block bg-purple-500 hover:bg-purple-600 text-white font-semibold py-3 px-8 rounded-xl transition-all"
                  >
                    Choose File
                  </label>
                  {file && (
                    <p className="mt-4 text-purple-200">Selected: {file.name}</p>
                  )}
                </div>
                <button
                  onClick={handleUpload}
                  disabled={loading || !file}
                  className="w-full bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white font-bold py-4 rounded-xl transition-all disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? 'Uploading...' : 'Upload Bill'}
                </button>
              </div>
            </div>
          )}

          {/* Inventory Tab */}
          {activeTab === 'inventory' && (
            <div>
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-3xl font-bold">Medicine Inventory</h2>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-purple-400" />
                  <input
                    type="text"
                    placeholder="Search medicines..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 pr-4 py-2 bg-white/5 border border-white/20 rounded-xl text-white placeholder-purple-300/50 focus:outline-none focus:ring-2 focus:ring-purple-500"
                  />
                </div>
              </div>

              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b border-white/20">
                      <th className="text-left py-3 px-4 text-purple-200 font-semibold">Product</th>
                      <th className="text-left py-3 px-4 text-purple-200 font-semibold">Batch</th>
                      <th className="text-left py-3 px-4 text-purple-200 font-semibold">Expiry</th>
                      <th className="text-left py-3 px-4 text-purple-200 font-semibold">Qty</th>
                      <th className="text-left py-3 px-4 text-purple-200 font-semibold">Status</th>
                      <th className="text-left py-3 px-4 text-purple-200 font-semibold">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredInventory.length === 0 ? (
                      <tr>
                        <td colSpan="6" className="text-center py-8 text-purple-300">
                          No inventory items found
                        </td>
                      </tr>
                    ) : (
                      filteredInventory
                        .filter(item => item.product_name && item.product_name.trim() !== '')
                        .map((item, idx) => (
                          <tr key={idx} className="border-b border-white/10 hover:bg-white/5 transition-colors">
                            <td className="py-4 px-4 text-white font-medium">{item.product_name}</td>
                            <td className="py-4 px-4 text-purple-200">{item.batch}</td>
                            <td className="py-4 px-4 text-purple-200">{item.exp}</td>
                            <td className="py-4 px-4">
                              <span className={`px-3 py-1 rounded-full text-sm font-semibold ${
                                parseInt(item.qty) < 10 ? 'bg-red-500/20 text-red-300' : 'bg-green-500/20 text-green-300'
                              }`}>
                                {item.qty}
                              </span>
                            </td>
                            <td className="py-4 px-4">
                              {item.expired ? (
                                <span className="px-3 py-1 rounded-full bg-red-500/20 text-red-300 text-sm font-semibold">
                                  Expired
                                </span>
                              ) : item.days_to_expiry !== null && item.days_to_expiry < 15 ? (
                                <span className="px-3 py-1 rounded-full bg-orange-500/20 text-orange-300 text-sm font-semibold">
                                  {item.days_to_expiry}d left
                                </span>
                              ) : (
                                <span className="px-3 py-1 rounded-full bg-green-500/20 text-green-300 text-sm font-semibold">
                                  Good
                                </span>
                              )}
                            </td>
                            <td className="py-4 px-4">
                              <button
                                onClick={() => handleDelete(item.product_name, item.batch)}
                                className="flex items-center gap-2 bg-red-500/20 hover:bg-red-500/30 text-red-300 px-4 py-2 rounded-lg transition-all"
                              >
                                <Trash2 className="w-4 h-4" />
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {/* Alerts Tab */}
          {activeTab === 'alerts' && (
            <div>
              <h2 className="text-3xl font-bold mb-6">Expiry Alerts</h2>
              {alerts.length === 0 ? (
                <div className="text-center py-12">
                  <AlertTriangle className="w-16 h-16 mx-auto mb-4 text-purple-400/50" />
                  <p className="text-purple-300 text-lg">No active alerts</p>
                </div>
              ) : (
                <div className="grid md:grid-cols-2 gap-4">
                  {alerts.map(alert => (
                    <div
                      key={alert.alert_id}
                      className="bg-red-500/10 border border-red-500/30 rounded-xl p-6 hover:bg-red-500/20 transition-all"
                    >
                      <div className="flex items-start gap-4">
                        <AlertTriangle className="w-8 h-8 text-red-400 flex-shrink-0 mt-1" />
                        <div className="flex-1">
                          <h3 className="text-xl font-bold text-white mb-2">{alert.product_name}</h3>
                          <div className="space-y-1 text-red-200">
                            <p>Batch: {alert.batch}</p>
                            <p>Expiry: {alert.exp}</p>
                            <p>Days Left: {alert.days_to_expiry}</p>
                            <p className="text-red-300 font-semibold mt-2">{alert.alert_type}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* Scan & Dispense Tab */}
          {activeTab === 'scan' && (
            <div>
              <h2 className="text-3xl font-bold mb-6">Scan QR & Dispense</h2>
              
              <div className="space-y-6">
                <div>
                  <label className="block text-purple-200 font-semibold mb-2">Prescription ID</label>
                  <div className="flex gap-4">
                    <input
                      type="text"
                      value={prescriptionId}
                      onChange={(e) => setPrescriptionId(e.target.value)}
                      placeholder="Enter or scan prescription ID"
                      className="flex-1 bg-white/5 border border-white/20 rounded-xl px-4 py-3 text-white placeholder-purple-300/50 focus:outline-none focus:ring-2 focus:ring-purple-500"
                    />
                    <button
                      onClick={handleViewPrescription}
                      disabled={loading || !prescriptionId.trim()}
                      className="flex items-center gap-2 bg-blue-500 hover:bg-blue-600 text-white font-semibold px-6 py-3 rounded-xl transition-all disabled:opacity-50"
                    >
                      <Eye className="w-5 h-5" />
                      View
                    </button>
                  </div>
                </div>

                {/* Prescription Details */}
                {prescriptionData && (
                  <div className="bg-white/5 border border-white/20 rounded-2xl p-6">
                    <h3 className="text-2xl font-bold mb-4 text-white">Prescription Details</h3>
                    
                    <div className="grid md:grid-cols-2 gap-4 mb-6">
                      <div>
                        <p className="text-purple-300 text-sm">Prescription ID</p>
                        <p className="text-white font-semibold">{prescriptionData.prescription_id}</p>
                      </div>
                      <div>
                        <p className="text-purple-300 text-sm">Patient ID</p>
                        <p className="text-white font-semibold">{prescriptionData.patient_id}</p>
                      </div>
                      <div>
                        <p className="text-purple-300 text-sm">Doctor</p>
                        <p className="text-white font-semibold">{prescriptionData.doctor_name}</p>
                      </div>
                      <div>
                        <p className="text-purple-300 text-sm">Status</p>
                        <span className={`inline-block px-3 py-1 rounded-full text-sm font-semibold ${
                          prescriptionData.status === 'dispensed' 
                            ? 'bg-green-500/20 text-green-300' 
                            : 'bg-blue-500/20 text-blue-300'
                        }`}>
                          {prescriptionData.status}
                        </span>
                      </div>
                    </div>

                    <div className="mb-6">
                      <h4 className="text-lg font-semibold text-white mb-3">Medications</h4>
                      <div className="space-y-2">
                        {prescriptionData.medications?.map((med, idx) => (
                          <div key={idx} className="bg-white/5 rounded-lg p-4 flex justify-between items-center">
                            <div>
                              <p className="text-white font-semibold">{med.product_name}</p>
                              <p className="text-purple-300 text-sm">Batch: {med.batch} | Dosage: {med.dosage || 'As directed'}</p>
                            </div>
                            <span className="bg-purple-500/20 text-purple-300 px-3 py-1 rounded-full text-sm font-semibold">
                              Qty: {med.qty}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>

                    {prescriptionData.status !== 'dispensed' && (
                      <button
                        onClick={handleDispense}
                        disabled={loading}
                        className="w-full bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 text-white font-bold py-4 rounded-xl transition-all disabled:opacity-50"
                      >
                        {loading ? 'Dispensing...' : 'Dispense Prescription'}
                      </button>
                    )}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
