import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Heart, Shield, Activity, ClipboardList, Package, FileText } from 'lucide-react';

export default function LandingPage() {
  const navigate = useNavigate();
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  const cards = [
    {
      title: "Patient Registration",
      icon: ClipboardList,
      description: "Register and manage patient records securely",
      path: "/patient-registration",
      color: "blue"
    },
    {
      title: "Pharmacy Management",
      icon: Package,
      description: "Manage inventory and medicine stock efficiently",
      path: "/pharmacy",
      color: "teal"
    },
    {
      title: "Doctor Prescription",
      icon: FileText,
      description: "Create digital prescriptions with QR codes",
      path: "/doctor",
      color: "indigo"
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-slate-100">
      {/* Header */}
      <header className="bg-white border-b border-slate-200 shadow-sm sticky top-0 z-50">
        <div className="container mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="bg-blue-600 p-2.5 rounded-lg shadow-md">
                <Heart className="w-7 h-7 text-white" fill="white" strokeWidth={2} />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-slate-800">LifeTag</h1>
                <p className="text-xs text-slate-500 font-medium">Medical Management System</p>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span className="text-sm text-slate-600 font-medium">System Active</span>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="container mx-auto px-6 py-16">
        {/* Hero Section */}
        <div className={`text-center mb-16 transition-all duration-700 ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 -translate-y-4'}`}>
          <div className="inline-flex items-center gap-2 bg-blue-50 border border-blue-200 px-4 py-2 rounded-full mb-6">
            <Activity className="w-4 h-4 text-blue-600" />
            <span className="text-sm font-semibold text-blue-700">Smart Healthcare Solution</span>
          </div>
          
          <h2 className="text-5xl font-bold text-slate-800 mb-4 leading-tight">
            Smart Medicine<br />Management System
          </h2>
          <p className="text-xl text-slate-600 mb-10 max-w-2xl mx-auto">
            Ensuring patient safety through digital tracking and intelligent expiry monitoring
          </p>
          
          {/* Feature Pills */}
          <div className="flex flex-wrap justify-center gap-4">
            <div className="flex items-center gap-2 bg-white px-5 py-2.5 rounded-full border border-slate-200 shadow-sm hover:shadow-md transition-shadow">
              <Shield className="w-4 h-4 text-blue-600" />
              <span className="text-sm font-semibold text-slate-700">Expiry Monitoring</span>
            </div>
            <div className="flex items-center gap-2 bg-white px-5 py-2.5 rounded-full border border-slate-200 shadow-sm hover:shadow-md transition-shadow">
              <Activity className="w-4 h-4 text-teal-600" />
              <span className="text-sm font-semibold text-slate-700">Real-time Tracking</span>
            </div>
            <div className="flex items-center gap-2 bg-white px-5 py-2.5 rounded-full border border-slate-200 shadow-sm hover:shadow-md transition-shadow">
              <Heart className="w-4 h-4 text-red-600" />
              <span className="text-sm font-semibold text-slate-700">Patient Safety</span>
            </div>
          </div>
        </div>

        {/* Action Cards */}
        <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {cards.map((card, index) => {
            const Icon = card.icon;
            return (
              <div
                key={index}
                className={`group cursor-pointer transition-all duration-500 ${
                  isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
                }`}
                style={{ transitionDelay: `${index * 100}ms` }}
                onClick={() => navigate(card.path)}
              >
                <div className="bg-white border border-slate-200 rounded-2xl p-8 h-full shadow-md hover:shadow-xl transition-all duration-300 group-hover:-translate-y-2">
                  <div className={`bg-${card.color}-50 border border-${card.color}-100 w-16 h-16 rounded-xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform duration-300`}>
                    <Icon className={`w-8 h-8 text-${card.color}-600`} strokeWidth={2} />
                  </div>
                  
                  <h3 className="text-2xl font-bold mb-3 text-slate-800">{card.title}</h3>
                  <p className="text-slate-600 leading-relaxed mb-6">{card.description}</p>
                  
                  <div className="flex items-center text-blue-600 group-hover:text-blue-700 font-semibold">
                    <span>Access Module</span>
                    <svg className="w-5 h-5 ml-2 transform group-hover:translate-x-2 transition-transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Footer Info */}
        <div className={`text-center mt-16 transition-all duration-700 delay-300 ${isLoaded ? 'opacity-100' : 'opacity-0'}`}>
          <div className="inline-flex items-center gap-3 bg-white border border-slate-200 px-6 py-3 rounded-xl shadow-sm">
            <div className="flex items-center gap-2">
              <div className="relative">
                <div className="w-2.5 h-2.5 bg-green-500 rounded-full animate-pulse"></div>
              </div>
              <span className="text-sm text-slate-700 font-medium">All Systems Operational</span>
            </div>
            <div className="w-px h-6 bg-slate-300"></div>
            <span className="text-sm text-slate-500">
              {new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' })}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}
